function toggleDashboardDayAndWeek() {
    if (window.location.pathname == "/dashboard") {
        setTimeout(() => document.querySelector(
            '.fm-day-week-btn'
        ).click(), 500)
    }
}
toggleDashboardDayAndWeek()
window.addEventListener(
    'popstate', 
    function () { toggleDashboardDayAndWeek() }
)
